    fun process1(data: String) = data

    fun work1(data: String) {
        val markers = data.windowed(4).filter {
            it.toSet().size == 4
        }

        val index = data.indexOf(markers.first()) + 4

        println("First marker ends at $index")
    }

    fun work2(data: String) {
